mapper.select:

        where d.del_flag = '0'
         AND parent_id = #{parentId} 
		  AND dept_name like concat('%', #{deptName}, '%') 
		 AND status = #{status} 
		 AND org_code = #{orgCode} 
		 AND type = #{type} 

		${params.dataScope}
		order by d.parent_id, d.order_num

select d.dept_id from sys_dept d left join sys_role_dept rd on d.dept_id = rd.dept_id where rd.role_id = #{roleId}
and d.dept_id not in (select d.parent_id from sys_dept d inner join sys_role_dept rd on d.dept_id = rd.dept_id and rd.role_id = #{roleId})
order by d.parent_id, d.order_num

mapper.update:
update sys_dept
parent_id = #{parentId}, dept_name = #{deptName}, ancestors = #{ancestors}, order_num = #{orderNum}, leader = #{leader}, phone = #{phone}, dept_url = #{deptUrl}, email = #{email}, status = #{status}, update_by = #{updateBy}, org_code = #{orgCode}, type = #{type}, org_type = #{orgType}, dept_introduce = #{deptIntroduce}, update_time = sysdate()
where dept_id = #{deptId}

update sys_dept set ancestors =

    when #{item.deptId} then #{item.ancestors}

where dept_id in

    #{item.deptId}
