mapper.select:
select u.user_id,u.dept_id, u.nick_name, u.user_type, u.user_name, u.email, u.avatar, u.phone_number, u.password, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, d.dept_name, d.leader from sys_user u left join sys_dept d on u.dept_id = d.dept_id where u.del_flag = '0'
AND u.user_name like concat('%', #{userName}, '%')
AND u.status = #{status}
AND u.phone_number like concat('%', #{phoneNumber}, '%')
AND date_format(u.create_time,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{endTime},'%y%m%d')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))
AND u.org_code = #{orgCode}
AND u.nick_name LIKE CONCAT ('%', #{nickName}, '%')
AND u.user_type = #{userType}

select u.user_id,u.dept_id, u.nick_name, u.user_type, u.user_name, u.email, u.avatar, u.phone_number, u.password, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.unit_name, u.remark, a.province AS province, a.city AS city, a.area AS area, a.address AS address from sys_user u left join t_address a on a.customer_user_id = u.user_id where u.del_flag = '0'
AND u.user_name like concat('%', #{userName}, '%')
AND u.status = #{status}
AND u.phone_number like concat('%', #{phoneNumber}, '%')
AND date_format(u.create_time,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{endTime},'%y%m%d')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))
AND u.org_code = #{orgCode}
AND u.nick_name LIKE CONCAT ('%', #{nickName}, '%')
AND u.user_type = #{userType}

mapper.update:
update sys_user
dept_id = #{deptId}, user_name = #{userName}, nick_name = #{nickName}, email = #{email}, phone_number = #{phoneNumber}, sex = #{sex}, unit_name = #{unitName}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, org_code = #{orgCode}, update_time = sysdate()
where user_id = #{userId}

mapper.select:
SELECT * FROM `sys_user` u
  WHERE u.del_flag = '0'
  AND u.user_name =  #{userName}
  AND u.password =  #{password}
ORDER BY u.create_time DESC

SELECT u.*  FROM sys_user u
LEFT JOIN sys_user_post up ON u.user_id = up.user_id
LEFT JOIN sys_post p ON up.post_id = p.post_id
WHERE u.del_flag = '0'
 AND p.post_code = #{postCode}
 AND u.dept_id = #{deptId}
 AND u.org_code = #{orgCode}

SELECT u.* FROM sys_user u ,sys_user_post up, sys_post p
WHERE u.del_flag = '0' AND p.post_code = #{post}
AND up.post_id =p.post_id AND up.user_id = u.user_id
 AND u.org_code=#{orgCode} 
 AND u.dept_id = #{deptId}

SELECT u.* FROM sys_user u ,sys_user_post up, sys_post p
WHERE u.del_flag = '0' AND p.post_code = #{post}
AND up.post_id =p.post_id AND up.user_id = u.user_id
 AND u.dept_id = #{deptId} 
  AND u.org_code = #{orgCode}
